// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;
import {IERC165} from './IERC165.sol';

/**
 * @title Minimal IERC2981 Royalty Standard interface
 * @dev See https://eips.ethereum.org/EIPS/eip-2981
 */
interface IERC2981 is IERC165 {
    /**
     * @dev Called with the sale price to determine how much royalty is owed and to whom.
     * @param tokenId - the NFT asset queried for royalty information
     * @param salePrice - the sale price of the NFT asset specified by `tokenId`
     * @return receiver - address of who should receive the royalties.
     * @return royaltyAmount - the royalty amount calculated given the `salePrice`.
     */
    function royaltyInfo(uint256 tokenId, uint256 salePrice) external view returns (address receiver, uint256 royaltyAmount);
}

